     AND L.VTYP = V.VTYPE                    
     AND A.ACCAT <> '4'                    
     AND L2.CLTCODE=A.CLTCODE                    
     --AND COSTNAME = RTRIM(@BRANCH)                    
  AND EXISTS(SELECT COSTCODE FROM #COSTMAST C WHERE L2.COSTCODE = C.COSTCODE)       
     AND L2.VTYPE <> 18                    
     AND L2.COSTCODE = C.COSTCODE                    
     AND  L.EDT BETWEEN @FDATE AND @TDATE + ' 23:59:59'                    
    ORDER BY                    
     L2.CLTCODE,                    
     VOUDT1,                    
     L.VTYP DESC,                    
     L.VNO                    
   END                    
 END                    
                    
