 VNO,                    
 NARRATION,                    
 DDNO,                    
 CLTCODE,                    
 LONGNAME,                    
 VDT,            
 VTYP,                    
 ACCAT,                    
 OPBAL,                    
 CROSAC,                    
 ACNAME,                    
 BRANCHCODE,                    
 LNO                    
