CREATE PROCEDURE [dbo].[rpt_acc_Marginledger]                                
                                 
@FDATE VARCHAR(11),            /* AS MMM DD YYYY */                                
@TDATE VARCHAR(11),            /* AS MMM DD YYYY */                                
@FCODE VARCHAR(10),                                
@TCODE VARCHAR(10),                                
@STRORDER VARCHAR(6),                                
