  [BOOKTYPE] [CHAR] (2) NULL ,                                  
  [VOUDT] [DATETIME] NULL ,                                  
  [EFFDT] [DATETIME] NULL ,                                  
  [ACNAME] [VARCHAR] (100) NULL ,                        
