  [VTYP] [SMALLINT] NULL ,                                  
  [VDT] [VARCHAR] (30) NULL ,                                  
  [EDT] [VARCHAR] (30) NULL ,                                              
  [LNO] [DECIMAL](5, 0) NULL,              
  [PDT] [DATETIME] NULL              
 ) ON [PRIMARY]                                  
                                
