-- --------------------------------------------------
-- PROCEDURE dbo.Rpt_acc_partyledger
-- --------------------------------------------------


--EXEC RPT_ACC_PARTYLEDGER_ALL 'AUG  1 2007','AUG  8 2007','0000','ZZZZ','ACCODE','VDT','BROKER','BROKER','', 'N','N'  
  
  
--EXEC RPT_ACC_PARTYLEDGER 'JAN  1 2010','JAN 25 2012','0','ZZZ','ACCODE','VDT','BROKER','BROKER',''        
        
CREATE PROCEDURE [DBO].[RPT_ACC_PARTYLEDGER]        
(          
 @FDATE VARCHAR(11),            /* AS MMM DD YYYY */                
 @TDATE VARCHAR(11),            /* AS MMM DD YYYY */                
 @FCODE VARCHAR(10),                
 @TCODE VARCHAR(10),                
 @STRORDER VARCHAR(8),                
 @SELECTBY VARCHAR(3),                
 @STATUSID VARCHAR(15),                
 @STATUSNAME VARCHAR(15),                
 @STRBRANCH VARCHAR(10),
 @FORPDF VARCHAR(1) = 'N'
)          
          
AS         
  
DECLARE                
 @@REPTYPE VARCHAR(1),  
 @@STRORDER VARCHAR(6)  
  
  
SELECT @@REPTYPE = .DBO.PIECE(@STRORDER, '~', 2)  
SELECT @@STRORDER = .DBO.PIECE(@STRORDER, '~', 1)  
  
IF @@REPTYPE = 'P'  
BEGIN  
 EXEC RPT_ACC_PARTYLEDGER_PARENT @FDATE, @TDATE, @FCODE, @TCODE, @@STRORDER, @SELECTBY, @STATUSID, @STATUSNAME, @STRBRANCH  
 RETURN  
END  
  
  
                
/*========================================================================          
      EXEC RPT_ACC_PARTYLEDGER_ALL           
            'NOV  1 2005',          
            'DEC 31 2005',          
            'A',          
            'ZZZ',          
            'ACCODE',          
            'VDT',          
            'BROKER',          
            'UNDEFINED',          
            ''          
========================================================================*/          
          
DECLARE           
 @@OPENDATE   AS VARCHAR(11)                
                
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED                
                
                
/*GETTING LAST OPENING DATE */                
      IF UPPER(@SELECTBY) = 'VDT'                
      BEGIN                
            SELECT           
                  @@OPENDATE =           
                  (           
                        SELECT           
                              LEFT(CONVERT(VARCHAR, ISNULL(MAX(VDT), 0), 109), 11)           
                        FROM LEDGER WITH(NOLOCK)           
                        WHERE VTYP = 18           
                              AND VDT < = @FDATE           
                  )           
      END                
      ELSE                
      BEGIN                
            SELECT           
                  @@OPENDATE =           
                  (           
                        SELECT           
                              LEFT(CONVERT(VARCHAR, ISNULL(MAX(EDT), 0), 109), 11)           
                        FROM LEDGER WITH(NOLOCK)           
                        WHERE VTYP = 18           
                              AND EDT < = @FDATE           
                  )           
      END                
                      
                
      /*CREATING BLANK TABLE FOR OPENING BALANCE*/                
            SELECT           
                  CLTCODE,           
                  OPPBAL = VAMT           
            INTO #OPPBALANCE           
            FROM LEDGER WITH(NOLOCK)           
            WHERE 1 = 2           
                      
                
      /*GETTING OPENING BALANCE*/                
      IF @SELECTBY = 'VDT'                
      BEGIN                
            IF @@OPENDATE = @FDATE                 
            BEGIN 
				  INSERT           
                  INTO #OPPBALANCE           
                  SELECT           
                        CLTCODE,           
                        OPPBAL = ISNULL(SUM(           
                        CASE           
                              WHEN UPPER(B.DRCR) = 'D'           
                              THEN B.VAMT           
                              ELSE -B.VAMT           
                        END          
                        ), 0)           
                  FROM LEDGER B WITH(NOLOCK)           
				  WHERE B.CLTCODE > = @FCODE           
                        AND B.CLTCODE < = @TCODE           
                        AND B.VDT LIKE @FDATE + '%'           
                        AND VTYP = 18           
                  GROUP BY CLTCODE           

            END                
            ELSE                
            BEGIN                
                  INSERT           
                  INTO #OPPBALANCE           
                  SELECT      
                        CLTCODE,           
                        OPPBAL = ISNULL(SUM(           
                        CASE           
                              WHEN UPPER(B.DRCR) = 'D'           
                       THEN B.VAMT           
                              ELSE -B.VAMT           
                        END          
                        ), 0)           
                  FROM LEDGER B WITH(NOLOCK)                         WHERE B.CLTCODE > = @FCODE           
                        AND B.CLTCODE < = @TCODE           
                        AND B.VDT > = @@OPENDATE + ' 00:00:00'           
                        AND VDT < @FDATE           
               GROUP BY CLTCODE           
            END                
      END                 
      ELSE                
      BEGIN                 
            IF @@OPENDATE = @FDATE                 
            BEGIN              
                  INSERT           
                  INTO #OPPBALANCE           
                  SELECT           
                        CLTCODE,           
OPBAL = SUM(OPBAL)           
                  FROM           
                        (           
                              SELECT           
                                    CLTCODE,           
                                    OPBAL = ISNULL(SUM(           
                                    CASE           
                                          WHEN UPPER(DRCR) = 'D'           
                                          THEN VAMT           
                                          ELSE -VAMT           
                                    END          
                                    ), 0)           
                              FROM LEDGER WITH(NOLOCK)           
                              WHERE CLTCODE = @FCODE           
                                    AND EDT LIKE @@OPENDATE + '%'           
                                    AND VTYP = 18           
                              GROUP BY CLTCODE           
                              UNION ALL           
                              SELECT           
                                    CLTCODE,           
                                    OPPBAL = ISNULL(SUM(           
                                    CASE           
                                          WHEN UPPER(B.DRCR) = 'C'           
                                          THEN B.VAMT           
                                          ELSE -B.VAMT           
                                    END          
                                    ), 0)           
                              FROM LEDGER B WITH(NOLOCK)           
                              WHERE B.CLTCODE > = @FCODE           
                                    AND B.CLTCODE < = @TCODE           
                                    AND B.VDT < @@OPENDATE          
                                    AND EDT >= @@OPENDATE           
                              GROUP BY CLTCODE           
                        )           
                        T           
                  GROUP BY CLTCODE           
            END              
            ELSE              
            BEGIN              
                  INSERT           
                  INTO #OPPBALANCE           
                  SELECT           
                        CLTCODE,           
                        OPBAL = SUM(OPBAL)           
                  FROM           
                        (           
                              SELECT           
        CLTCODE,           
                                    OPBAL = ISNULL(SUM(           
                                    CASE           
                                          WHEN UPPER(DRCR) = 'D'           
                                          THEN VAMT           
                                          ELSE -VAMT           
                                    END          
                                    ), 0)           
               FROM LEDGER WITH(NOLOCK)           
                              WHERE CLTCODE = @FCODE           
                                    AND EDT LIKE @@OPENDATE + '%'           
                                    AND VTYP = 18           
                              GROUP BY CLTCODE           
                              UNION ALL           
                   SELECT           
                                    CLTCODE,           
                                    OPBAL = SUM(           
                                    CASE           
                                          WHEN UPPER(DRCR) = 'D'           
                                          THEN VAMT           
                                          ELSE -VAMT         
                                    END          
                                    )           
                              FROM LEDGER WITH(NOLOCK)           
                              WHERE CLTCODE = @FCODE           
                                    AND EDT > = @@OPENDATE + ' 00:00:00'           
                                    AND EDT < @FDATE           
                                    AND VTYP <> 18           
                              GROUP BY CLTCODE           
                              UNION ALL           
                              SELECT           
                                    CLTCODE,           
                                    OPPBAL = ISNULL(SUM(           
                                    CASE           
                                          WHEN UPPER(B.DRCR) = 'C'           
                                          THEN B.VAMT           
      ELSE -B.VAMT           
                                    END          
                                    ), 0)           
                              FROM LEDGER B WITH(NOLOCK)           
                              WHERE B.CLTCODE > = @FCODE           
                                    AND B.CLTCODE < = @TCODE           
                                    AND B.VDT < @@OPENDATE           
                                    AND EDT >= @@OPENDATE           
                              GROUP BY CLTCODE           
                        )           
                        T           
                  GROUP BY CLTCODE           
            END              
      END                
                      
      /*GENERATING BLANK STRUCTURE FOR FILTERED LEDGER */                
            SELECT           
                  VTYP,VNO,EDT,LNO,ACNAME,DRCR,VAMT,VDT,VNO1,REFNO,BALAMT,NODAYS,CDT,CLTCODE,BOOKTYPE,ENTEREDBY,PDT,CHECKEDBY,ACTNODAYS,L.NARRATION,           
                  SHORTDESC = SPACE(35)           
            INTO #LEDGERDATA           
            FROM LEDGER L WITH(NOLOCK)           
            WHERE 1 = 2           
  
                     
                
                
      /*GETTING FIILTERED LEDGER*/                
      IF @SELECTBY = 'VDT'                
      BEGIN                
            INSERT           
            INTO #LEDGERDATA           
            SELECT           
                  VTYP,VNO,EDT,LNO,ACNAME,DRCR,VAMT,VDT,VNO1,REFNO,BALAMT,NODAYS,CDT,CLTCODE,BOOKTYPE,ENTEREDBY,PDT,CHECKEDBY,ACTNODAYS,L.NARRATION,           
                  ISNULL(V.SHORTDESC, '') SHORTDESC           
            FROM LEDGER L WITH(NOLOCK),           
                  VMAST V WITH(NOLOCK)           
            WHERE VDT > = @FDATE + ' 00:00:00'           
                  AND L.VTYP = V.VTYPE           
                  AND VDT < = @TDATE +' 23:59:59'           
                  AND CLTCODE > = @FCODE           
                  AND CLTCODE < = @TCODE           
      END                 
      ELSE                
      BEGIN                
            INSERT           
            INTO #LEDGERDATA           
            SELECT           
                  VTYP,VNO,EDT,LNO,ACNAME,DRCR,VAMT,VDT,VNO1,REFNO,BALAMT,NODAYS,CDT,CLTCODE,BOOKTYPE,ENTEREDBY,PDT,CHECKEDBY,ACTNODAYS,L.NARRATION,           
                  ISNULL(V.SHORTDESC, '') SHORTDESC           
            FROM LEDGER L WITH(NOLOCK),           
                  VMAST V WITH(NOLOCK)           
            WHERE EDT > = @FDATE + ' 00:00:00'           
                  AND L.VTYP = V.VTYPE           
                  AND EDT < = @TDATE +' 23:59:59'           
                  AND CLTCODE > = @FCODE           
                  AND CLTCODE < = @TCODE           
      END                
                
          
          
/*GETTING CLIENT LIST*/                
      SELECT           
            C2.PARTY_CODE,          
   C1.LONG_NAME,          
            BANK_NAME = ISNULL(C4.BANKID, ''),           
            L_ADDRESS1,           
            L_ADDRESS2,           
            L_ADDRESS3,           
            L_CITY,           
            L_ZIP,           
            RES_PHONE1,           
            C1.BRANCH_CD,           
           FAMILY,           
            C1.SUB_BROKER,           
            TRADER,           
            CL_TYPE,           
            CLTDPID = ISNULL(CLTDPID, '')           
      INTO #LEDGERCLIENTS           
      FROM NSECURFO.DBO.CLIENT1 C1 WITH(NOLOCK),           
            NSECURFO.DBO.CLIENT2 C2 WITH(NOLOCK)           
      LEFT OUTER JOIN           
            NSECURFO.DBO.CLIENT4 C4 WITH(NOLOCK)           
            ON           
            (          
                  C2.PARTY_CODE = C4.PARTY_CODE           
                  AND DEPOSITORY NOT IN ('NSDL', 'CDSL')           
				  AND DEFDP = 1          
--                  AND DEFDP = 0      
            )           
      WHERE C1.CL_CODE = C2.CL_CODE           
            AND C1.BRANCH_CD LIKE (          
            CASE           
                  WHEN @STATUSID = 'BRANCH'           
                  THEN @STATUSNAME           
                  ELSE '%'           
            END          
            )
            AND SUB_BROKER LIKE (          
            CASE           
                  WHEN @STATUSID = 'SUB_BROKER'           
                  THEN @STATUSNAME           
                  ELSE '%'           
            END          
            )           
			AND SUB_BROKER LIKE (          
            CASE           
                  WHEN @STATUSID = 'SUBBROKER'           
                  THEN @STATUSNAME           
                  ELSE '%'           
            END          
            )           
            AND TRADER LIKE (          
            CASE           
                  WHEN @STATUSID = 'TRADER'           
                  THEN @STATUSNAME           
                  ELSE '%'           
            END          
            )           
            AND C1.FAMILY LIKE (          
            CASE           
                  WHEN @STATUSID = 'FAMILY'           
                  THEN @STATUSNAME           
                  ELSE '%'           
            END          
            )           
            AND C2.PARTY_CODE LIKE (          
            CASE           
                  WHEN @STATUSID = 'CLIENT'           
                  THEN @STATUSNAME           
                  ELSE '%'           
            END          
         )           
            AND C1.BRANCH_CD LIKE @STRBRANCH +'%'           
            AND C2.PARTY_CODE > = @FCODE           
            AND C2.PARTY_CODE < = @TCODE           
          
          
      CREATE TABLE [#TMPLEDGER] (          
       [BOOKTYPE] [CHAR] (2)  NULL ,          
       [VOUDT] [DATETIME] NULL ,          
       [EFFDT] [DATETIME] NULL ,          
       [SHORTDESC] [VARCHAR] (35)  NULL ,          
       [DRAMT] [MONEY] NULL ,          
       [CRAMT] [MONEY] NULL ,          
       [VNO] [VARCHAR] (12)  NULL ,          
       [DDNO] [VARCHAR] (15)  NULL ,          
       [NARRATION] [VARCHAR] (234)  NULL ,          
       [CLTCODE] [VARCHAR] (10)  NOT NULL ,          
       [VTYP] [SMALLINT] NULL ,          
       [VDT] [VARCHAR] (30)  NULL ,          
       [EDT] [VARCHAR] (30)  NULL ,          
       [ACNAME] [VARCHAR] (100)  NULL ,          
       [OPBAL] [MONEY] NULL ,          
       [L_ADDRESS1] [VARCHAR] (40)  NOT NULL ,          
       [L_ADDRESS2] [VARCHAR] (40)  NULL ,          
       [L_ADDRESS3] [VARCHAR] (40)  NULL ,          
       [L_CITY] [VARCHAR] (40)  NULL ,          
       [L_ZIP] [VARCHAR] (10)  NULL ,          
       [RES_PHONE1] [VARCHAR] (15)  NULL ,          
       [BRANCH_CD] [VARCHAR] (10)  NOT NULL ,          
       [CROSAC] [VARCHAR] (10)  NULL ,          
       [EDIFF] [INT] NULL ,          
       [FAMILY] [VARCHAR] (10)  NOT NULL ,          
       [SUB_BROKER] [VARCHAR] (10)  NOT NULL ,          
       [TRADER] [VARCHAR] (20)  NOT NULL ,          
       [CL_TYPE] [VARCHAR] (3)  NOT NULL ,          
       [BANK_NAME] [VARCHAR] (50)  NOT NULL ,          
       [CLTDPID] [VARCHAR] (20)  NOT NULL ,          
       [LNO] [INT] NULL,        
       [PDT] [DATETIME] NULL
      ) ON [PRIMARY]          
   
           
/*GETTING LDDGER ENTRIES*/                
      INSERT           
            INTO #TMPLEDGER           
      SELECT           
            L.BOOKTYPE,           
 VOUDT = L.VDT,           
            EFFDT = L.EDT,           
            L.SHORTDESC,           
            DRAMT = (          
            CASE           
                  WHEN UPPER(L.DRCR) = 'D'           
                  THEN L.VAMT           
                  ELSE 0           
            END          
            ),           
            CRAMT = (          
            CASE           
                  WHEN UPPER(L.DRCR) = 'C'           
                  THEN L.VAMT           
                  ELSE 0           
            END          
            ),           
            L.VNO,           
            DDNO = SPACE(15),           
            L.NARRATION,           
            C.PARTY_CODE CLTCODE,           
            VTYP = ISNULL(L.VTYP, 18),           
            CONVERT(VARCHAR, L.VDT, 103) VDT,           
            CONVERT(VARCHAR, L.EDT, 103) EDT,           
--            L.ACNAME ,           
            C.LONG_NAME,          
            OPBAL = VAMT,           
            C.L_ADDRESS1,           
            C.L_ADDRESS2,           
            C.L_ADDRESS3,           
            C.L_CITY,           
            C.L_ZIP,           
            C.RES_PHONE1,           
            C.BRANCH_CD,           
            L.CLTCODE CROSAC ,           
            EDIFF = DATEDIFF(D, L.EDT, GETDATE()),           
            C.FAMILY,           
            C.SUB_BROKER,           
            C.TRADER,           
            C.CL_TYPE,           
            C.BANK_NAME,           
            C.CLTDPID,           
            L.LNO,        
			L.PDT
      FROM #LEDGERDATA L WITH(NOLOCK)           
      RIGHT OUTER JOIN           
            #LEDGERCLIENTS C WITH(NOLOCK)           
            ON           
            (          
                  L.CLTCODE = C.PARTY_CODE          
            )           
          
          
                
/*UPDATING OPENING BLANACE*/                
      UPDATE           
            #TMPLEDGER           
            SET OPBAL = 0           
          
      UPDATE           
            #TMPLEDGER           
            SET OPBAL = T.OPPBAL           
      FROM #TMPLEDGER L WITH(NOLOCK),           
            #OPPBALANCE T WITH(NOLOCK)           
