                        Acmast A WITH(NoLock)               
                  WHERE L.Cltcode = A.Cltcode               
                        AND Accat In ('4', '104')           
                  ORDER BY L.Acname,             
                        Voudt, Pdt               
            End                    
            Else                    
            Begin                    
                  SELECT               
                        L.*               
                  FROM #tmpledger L WITH(NoLock),               
                        Acmast A WITH(NoLock)               
                  WHERE L.Cltcode = A.Cltcode               
                        AND Accat In ('4', '104')               
                  ORDER BY L.Acname,              
                        Effdt, Pdt            
            End                    
      End                    
                    
