IF @BULK = 'N'  
 BEGIN  
  IF @FLAG = 'CODEWISE'      
   BEGIN    
    SELECT * FROM #TEMPLEDGERFINAL ORDER BY  CLTCODE, ACNAME, BRANCHCODE    
   END    
  ELSE    
   BEGIN    
    SELECT * FROM #TEMPLEDGERFINAL  ORDER BY  ACNAME,CLTCODE, BRANCHCODE    
