create PROCEDURE  rpt_accpandl  
  
@fromdt datetime,  
@todt datetime,  
@statusid varchar(15),  
@statusname varchar(25)  
  
  
as  
  
  
if @statusid='broker'  
begin  
 select g1.grpname,g1.grpcode,   
