 from ACCOUNTCURFO.dbo.grpmast g1   
 where g1.grpcode='N0000000000' or  g1.grpcode='X0000000000'   
 order by g1.grpcode  
end  
else  
begin  
 select g1.grpname,g1.grpcode,   
 vamt=isnull(( select sum(case when l.drcr ='c' then vamt*-1 else vamt end )from  
  ACCOUNTCURFO.dbo.ledger l ,ACCOUNTCURFO.dbo.acmast a ,ACCOUNTCURFO.dbo.grpmast g ,  ACCOUNTCURFO.dbo.ledger2 l2 ,  ACCOUNTCURFO.dbo.costmast c , ACCOUNTCURFO.dbo.category c1  
