-- --------------------------------------------------
-- PROCEDURE dbo.rpt_GrpCombineTrialBalance
-- --------------------------------------------------




--EXEC rpt_GrpCombineTrialBalance 'Apr  1 2005', 'Dec 31 2005', 'NSXFO', ''
CREATE  Proc rpt_GrpCombineTrialBalance    
(    
@FromDate varchar(11),    
@ToDate varchar(11),    
@Segment Varchar(5)='ALL',    
@BranchCd Varchar(10)=''    
)    
    
as     
    
    
Declare @CodeStrip varchar(11)    
Declare @GrpName varchar(100)    
Declare @GrpCode varchar(15)    
Declare @TBCur Cursor      
    
    
 
CREATE 
    Table #TrialBalance 
	( 
		Segment varchar(5), 
		CltCode varchar(10), 
		ACName varchar(100), 
		GrpCode varchar(20), 
		GrpName varchar(100), 
		Amount money, 
		DrAmount money, 
		CrAmount money, 
		BranchCode varchar(20) 
	)     
	if @Segment = '' begin 
		SET @Segment = 'ALL' 
	END                             
	if @Segment = 'ALL' or @Segment = 'NSECM'    
		begin    
 
			SET transaction isolation level read uncommitted 
			INSERT 
			INTO #TrialBalance 
			SELECT 
				Segment, 
				CltCode, 
				AcName, 
				A.GrpCode, 
				B.GrpName, 
				Amount = VAmt, 
				DrAmount = (
				CASE 
					WHEN Vamt >= 0 
					THEN abs(Vamt) 
					ELSE 0 
				END
				), 
				CrAmount = (
				CASE 
					WHEN Vamt < 0 
					THEN Abs(Vamt) 
					ELSE 0 
				END
				), 
				'' 
			FROM 
				( 
				SELECT 
					Segment = 'NSECM', 
					GrpCode = A.grpcode, 
					CltCode = a.CltCode, 
					AcName = a.acname, 
					Vamt = sUM(
					CASE 
						WHEN DRCR = 'D' 
						THEN Vamt 
						ELSE -vamt 
					END
					) 
				FROM AccountFo.dbo.ledger l, --with(index(ledind),nolock),     
					AccountFo.dbo.acmast a --with(index(accat),nolock)                 
				WHERE l.cltcode = a.cltcode 
					AND vdt >= @FromDate + ' 00:00:00' 
					AND vdt <= @ToDate + ' 23:59:59' 
					AND accat not in (4,104,204) 
				GROUP BY a.GrpCode,
					A.cltcode, 
					a.ACName 
				) 
				A, 
				AccountFo.dbo.GrpMast b (nolock) 
