    FROM                        
     (                        
      SELECT                        
       L2.CLTCODE,                        
       SUM(CASE L2.DRCR WHEN 'D' THEN CAMT ELSE -CAMT END) AS OPBAL                        
      FROM                        
       LEDGER2 L2,                        
       Sledger_Vw_Final  L --, COSTMAST C                      
      WHERE                        
       VDT LIKE @@OPENDATE + '%' AND VTYPE = 18                
