		LTRIM(RTRIM(BRANCHNAME)) + ' CONTROL A/C', 
		LTRIM(RTRIM(BRANCHNAME)) + ' CONTROL A/C', 
		'ASSET', 
		'3', 
		'', 
		LTRIM(RTRIM(BRCONTROLAC)), 
		'', 
		'A0000000000', 
		'', 
		'0', 
		LTRIM(RTRIM(BRANCHNAME)), 
		'0', 
		'C', 
		'', 
		'', 
		'' 
	FROM ACCOUNTCURFO.DBO.BRANCHACCOUNTS 
	WHERE BRCONTROLAC NOT IN 
		(
		SELECT DISTINCT 
			CLTCODE 
		FROM ACCOUNTCURFO.DBO.ACMAST
		)

GO

