CREATE PROCEDURE [DBO].[V2_BULKPAYOUTPOSTING_SEBI]
(
	@SESSIONID VARCHAR(50),
	@UNAME VARCHAR(50),
	@S_SESSIONID VARCHAR(50),
	@GENFLAG VARCHAR(1),
	@SEGMENT VARCHAR(6)
)

AS

	-- EXEC ACCOUNT.DBO.V2_BULKPAYOUTPOSTING_SEBI '84449309411212008', 'DEMO', '844493094', 'A', 'NSECM' 

	SET NOCOUNT ON

	DECLARE
		@@NEWVNO AS VARCHAR(12),
		@@MAXCOUNT AS INT,
		@@VDT AS VARCHAR(11),
		@@BANKBRANCH AS VARCHAR(10),
		@@BANKCOST AS NUMERIC,
		@@VNOCUR AS CURSOR,
		@@LNOCUR AS CURSOR,
		@@LNOVNO AS VARCHAR(12),
		@@ERROR_COUNT INT,
		@@STD_DATE VARCHAR(11),
		@@LST_DATE VARCHAR(11),
		@@VNOMETHOD INT,
		@@ACNAME CHAR(100),
		@@BOOKTYPE CHAR(2),
		@@MICRNO VARCHAR(10),
		@@DRCR VARCHAR(1),
		@@VTYP SMALLINT,
		@@BNKCODE VARCHAR(100),
		@@MAXVNO INT,
		@@SQL VARCHAR(MAX),
		@@EXCH CHAR(3),
		@@SEG CHAR(7)

	IF @SEGMENT = 'NSECM'
	BEGIN
		SET @@EXCH = 'NSE'
		SET @@SEG = 'CAPITAL'
	END

	IF @SEGMENT = 'BSECM'
	BEGIN
		SET @@EXCH = 'BSE'
		SET @@SEG = 'CAPITAL'
	END

	IF @SEGMENT = 'NSEFUT'
	BEGIN
		SET @@EXCH = 'NSE'
		SET @@SEG = 'FUTURES'
	END

	IF @SEGMENT = 'BSEFUT'
	BEGIN
		SET @@EXCH = 'BSE'
		SET @@SEG = 'FUTURES'
	END

	IF @SEGMENT = 'NSXFUT'
	BEGIN
		SET @@EXCH = 'NSX'
		SET @@SEG = 'FUTURES'
	END

	IF @SEGMENT = 'MCDFUT'
	BEGIN
		SET @@EXCH = 'MCD'
		SET @@SEG = 'FUTURES'
	END
	-------------------------- UPDATE BANK CODE DETAILS ------------------------
	UPDATE
		P
	SET 
		BNKNAME = A.ACNAME,
		BRANCHCODE = A.BRANCHCODE,
		MICRNO = A.MICRNO,
		BOOKTYPE = A.BOOKTYPE,
		ACCDTLS = A.ACCDTLS,
		UPDFLAG = 'Y'
	FROM 
		V2_SEBIPAYOUT_TEMP P,
		ACMAST A
	WHERE 
		P.BNKCODE = A.CLTCODE
		AND ACCAT = 2
		AND SESSIONID = @SESSIONID

	UPDATE
		P
	SET 
		COSTCODE = C.COSTCODE,
		ACNAME = A.LONGNAME
	FROM 
		ACMAST A,
		COSTMAST C,
		V2_SEBIPAYOUT_TEMP P
	WHERE 
		A.CLTCODE = ACCODE
		AND ACCAT IN ('4')
		AND A.BRANCHCODE = C.COSTNAME
		AND SESSIONID = @SESSIONID

	UPDATE
		P
	SET 
		BANKCOST = C.COSTCODE
	FROM 
		ACMAST A,
		COSTMAST C,
		V2_SEBIPAYOUT_TEMP P
	WHERE 
		A.CLTCODE = BNKCODE
		AND ACCAT IN ('2')
		AND A.BRANCHCODE = C.COSTNAME
		AND SESSIONID = @SESSIONID

	UPDATE
		V2_SEBIPAYOUT_TEMP
	SET 
		BANKCOST = COSTCODE
	WHERE 
		BANKCOST = ''
		AND SESSIONID = @SESSIONID

	-------------------------- VALIDATION BASED ON UPDFLAG ------------------------
	SELECT @@ERROR_COUNT = COUNT(1) FROM
	(
		SELECT DISTINCT 
			ACCODE
		FROM 
			V2_SEBIPAYOUT_TEMP WITH(NOLOCK)
		WHERE 
			UPDFLAG = 'N' 
			AND SESSIONID = @SESSIONID
	) A

	IF @@ERROR_COUNT > 0
	BEGIN
		SELECT 
			'FOLLOWING CLIENTS DO NOT EXIST<BR>'
		UNION ALL
		SELECT DISTINCT 
			ACCODE
		FROM 
			V2_SEBIPAYOUT_TEMP WITH(NOLOCK)
		WHERE 
			UPDFLAG = 'N' 
			AND SESSIONID = @SESSIONID
		DELETE FROM 
			V2_SEBIPAYOUT_TEMP 
		WHERE 
			SESSIONID = @SESSIONID
		RETURN
	END

	--------------------------AUTO GENERATION OF VNO ------------------------
	SELECT
		@@STD_DATE = LEFT(SDTCUR, 11),
		@@LST_DATE = LEFT(LDTCUR, 11),
		@@VNOMETHOD = VNOFLAG
	FROM 
		PARAMETER
	WHERE 
		CURYEAR = 1     

	SET @@VNOCUR = CURSOR FOR
	SELECT DISTINCT
		BNKCODE,
		BOOKTYPE,
		VTYP
	FROM
		V2_SEBIPAYOUT_TEMP WITH(NOLOCK)
	WHERE
		SESSIONID = @SESSIONID

	OPEN @@VNOCUR
	FETCH NEXT
	FROM
		@@VNOCUR
	INTO
		@@BNKCODE,
		@@BOOKTYPE,
		@@VTYP
	WHILE @@FETCH_STATUS = 0

	BEGIN
		CREATE TABLE
		#BANK_VNO
		(
			SRNO INT,
			NEWSRNO INT IDENTITY (1, 1)
		)

	INSERT INTO
		#BANK_VNO
	SELECT DISTINCT 
		SRNO
	FROM
		V2_SEBIPAYOUT_TEMP WITH(NOLOCK)
	WHERE
		BNKCODE = @@BNKCODE
		AND BOOKTYPE = @@BOOKTYPE
		AND SESSIONID = @SESSIONID

	SELECT
		@@MAXCOUNT = COUNT(DISTINCT SRNO)
	FROM
		V2_SEBIPAYOUT_TEMP WITH(NOLOCK)
	WHERE
		SESSIONID = @SESSIONID

	SELECT TOP 1
		@@VDT = VDT
	FROM
		V2_SEBIPAYOUT_TEMP WITH(NOLOCK)
	WHERE
		SESSIONID = @SESSIONID

	SELECT  
		LASTVNO  
	INTO 
		#VNO  
	FROM  
		LASTVNO  
	WHERE  
		1 = 2

	SELECT @@MAXVNO = MAX(NEWSRNO) FROM #BANK_VNO  

	INSERT INTO 
		#VNO
	EXEC 
		ACC_GENVNO_NEW
		@@VDT,
		@@VTYP,
		@@BOOKTYPE,
		@@STD_DATE,
		@@LST_DATE,  
		@@MAXVNO  

	SELECT  
		@@NEWVNO = LASTVNO  
	FROM  
		#VNO

	UPDATE
		P
	SET 
		VNO = CONVERT(VARCHAR(12),CONVERT(NUMERIC,@@NEWVNO) + (NEWSRNO - 1)) 
	FROM
		V2_SEBIPAYOUT_TEMP P WITH(NOLOCK),
		#BANK_VNO BV
	WHERE
		P.SRNO = BV.SRNO 
		AND SESSIONID = @SESSIONID

	DROP TABLE #VNO  
	DROP TABLE #BANK_VNO

	FETCH NEXT  
	FROM @@VNOCUR  
	INTO  
		@@BNKCODE,  
		@@BOOKTYPE,
		@@VTYP  
	END  

	DEALLOCATE @@VNOCUR

	--------------------------AUTO GENERATION OF LNO ------------------------
	UPDATE
		V2_SEBIPAYOUT_TEMP
	SET 
		LNO = 2
	WHERE 
		VNO IN
	(
		SELECT
			VNO
		FROM 
			V2_SEBIPAYOUT_TEMP WITH(NOLOCK)
		WHERE 
			SESSIONID = @SESSIONID
		GROUP BY 
			VNO
		HAVING 
			COUNT(*) = 1
	)

	SET @@LNOCUR = CURSOR FOR
	SELECT
		VNO
	FROM 
		V2_SEBIPAYOUT_TEMP WITH(NOLOCK) 
	WHERE 
		SESSIONID = @SESSIONID
	GROUP BY 
		VNO
	HAVING 
