	OPEN @@LNOCUR
	FETCH NEXT FROM @@LNOCUR INTO @@LNOVNO
	WHILE @@FETCH_STATUS = 0

	BEGIN
		CREATE TABLE [#LNOGEN] 
		(
			VNO VARCHAR(12),
			SNO INT,
			LNO INT IDENTITY(1,1)
		)

		INSERT INTO 
			#LNOGEN
		SELECT
