		FETCH NEXT FROM @@LNOCUR INTO @@LNOVNO
	END

	CLOSE @@LNOCUR
	DEALLOCATE @@LNOCUR

	BEGIN TRANSACTION

	--------------------------BEGIN POSTING TO TRANSACTION TABLES ------------------------
	INSERT INTO 
