-- --------------------------------------------------
-- PROCEDURE dbo.V2_CANNEDLEDGERBALANCE
-- --------------------------------------------------


CREATE PROCEDURE V2_CANNEDLEDGERBALANCE(
                @SDATE   VARCHAR(11),
                @EFFDATE VARCHAR(11))

AS

  DECLARE  @@RUNDATE DATETIME
                     
  SET @@RUNDATE = GETDATE()
                  
  TRUNCATE TABLE CANNED_LEDGERBALANCE
  
  INSERT INTO CANNED_LEDGERBALANCE
  SELECT   L.CLTCODE,
           LEDBAL = SUM(CASE 
                          WHEN DRCR = 'D' THEN -(VAMT)
                          ELSE (VAMT)
                        END),
           ENTRYTYPE = 1,
           @@RUNDATE
  FROM     LEDGER L WITH (NOLOCK),
           ACMAST A WITH (NOLOCK)
  WHERE    L.CLTCODE = A.CLTCODE
           AND A.ACCAT = 4
           AND L.VDT BETWEEN @SDATE + ' 00:00:00'
                             AND @EFFDATE + ' 23:59:59'
           AND L.EDT <= @EFFDATE + ' 23:59:59'
  GROUP BY L.CLTCODE
  HAVING   SUM(CASE 
                 WHEN DRCR = 'D' THEN -(VAMT)
                 ELSE (VAMT)
               END) <> 0
                       
  INSERT INTO CANNED_LEDGERBALANCE
  SELECT   L.CLTCODE,
           LEDBAL = SUM(CASE 
                          WHEN DRCR = 'D' THEN -(VAMT)
                          ELSE 0
                        END),
           ENTRYTYPE = 2,
           @@RUNDATE
  FROM     LEDGER L WITH (NOLOCK),
           ACMAST A WITH (NOLOCK)
  WHERE    L.CLTCODE = A.CLTCODE
           AND A.ACCAT = 4
           AND L.VDT BETWEEN @SDATE + ' 00:00:00'
                             AND @EFFDATE + ' 23:59:59'
           AND EDT > @EFFDATE + ' 23:59:59'
  GROUP BY L.CLTCODE
  HAVING   SUM(CASE 
                 WHEN DRCR = 'D' THEN -(VAMT)
                 ELSE 0
               END) <> 0 
           
  INSERT INTO CANNED_LEDGERBALANCE
  SELECT   L.CLTCODE,
           LEDBAL = SUM(CASE 
                          WHEN DRCR = 'D' THEN 0
                          ELSE -(VAMT)
                        END),
           ENTRYTYPE = 3,
           @@RUNDATE
  FROM     LEDGER L WITH (NOLOCK),
           ACMAST A WITH (NOLOCK)
  WHERE    L.CLTCODE = A.CLTCODE
           AND A.ACCAT = 4
           AND L.EDT >= @EFFDATE + ' 23:59:59'
           AND L.VDT < @SDATE
  GROUP BY L.CLTCODE
  HAVING   SUM(CASE 
                 WHEN DRCR = 'D' THEN 0
                 ELSE -(VAMT)
               END) <> 0

GO

-- --------------------------------------------------
-- PROCEDURE dbo.V2_CapAdqMargin_Balances
-- --------------------------------------------------

CREATE Proc V2_CapAdqMargin_Balances 
(
      @SDate Varchar(11), 
      @FParty Varchar(10), 
      @TParty Varchar(10), 
      @CAMarg Varchar(10), 
      @Marg Varchar(10)
) 

/*=================================================================================================
Exec V2_CapAdqMargin_Balances 
      @SDate = 'Jun 13 2006', 
      @FParty = '0000000000', 
      @TParty = 'ZZZZZZZZZZ', 
      @CAMarg = '', 
      @Marg = ''
=================================================================================================*/

As

Set Transaction Isolation Level Read Uncommitted

Set NoCount On 

CREATE TABLE [#Margin] 
(
	[SNo] [int] IDENTITY (1, 1) NOT NULL ,
	[Party_Code] [varchar] (10) NOT NULL ,
	[AcName] [varchar] (100) NULL ,
	[CapAdqBal] [money] NULL ,
	[MarginBal] [money] NULL 
) ON [PRIMARY]

      Insert Into #Margin 
      Select 
            Party_Code, 
            AcName, 
            CapAdqBal = Sum(
                              Case 
                              When MCltCode = @CAMarg 
                              Then ( 
                                    Case 
                                    When DrCr = 'D' 
                                    Then -Amount 
                                    Else Amount 
                                    End)
                              Else 0 
                              End), 
            MarginBal = Sum(
                              Case 
                              When MCltCode = @Marg 
                              Then ( 
                                    Case 
                                    When DrCr = 'D' 
                                    Then -Amount 
                                    Else Amount 
                                    End)
                              Else 0 
                              End) 
      From 
            MarginLedger M, 
            AcMast A, 
            Parameter P 
      Where 
            M.Party_Code = A.CltCode 
            And A.AcCat = 4 
            And A.CltCode Between @FParty And @TParty 
            And Vdt <= @SDate + ' 23:59:59' 
            And @SDate Between SdtCur And LdtCur 
            And VDt Between SdtCur And LdtCur 
      Group By 
            Party_Code, 
            AcName 
      Order By 
            Party_Code 


      Select 
            SerialNo = SNo, 
            ClientCode = Party_Code, 
            ClientName = AcName, 
            CapAdqBal, 
            MarginBal, 
            TotalBal = CapAdqBal + MarginBal 
      From 
            #Margin 

      Drop Table #Margin

GO

