	@USER VARCHAR(25),
	@EXCHANGE VARCHAR(3),
	@SEGMENT VARCHAR(7),
	@EFFDATE VARCHAR(11),
	@COMP_FOR VARCHAR(6) = 'BRANCH' -- COMPUTATION FOR BRANCH REQUESTING / ONLINE CLIENTS PAYOUT
)

AS

	/*===========================================================================
		---------------------------------------------------------------------
		FNO CREDIT BALANCES AFTER ADJUSTING
		MARGIN REQUIREMENT & COLLATERAL
		TO BE CONSIDERED WHILE COMPUTING NET BALANCE
		---------------------------------------------------------------------

		EXEC V2_GETLEDGERBALANCE_NEW
			@SDATE = 'APR  1 2005',
			@FPARTY = '',
			@TPARTY = '',
			@STLNO = '',
			@GENFLAG = 'A',
			@USER = 'PM',
			@EXCHANGE = 'NSX',
			@SEGMENT = 'CAPITAL',
			@EFFDATE = 'MAR 31 2006',
			@COMP_FOR = 'BRANCH'

		SELECT * FROM V2_LEDGERBALANCE

		SELECT PARTYCODE FROM V2_LEDGERBALANCE GROUP BY PARTYCODE HAVING COUNT(*) > 1
		
	===========================================================================*/

	SET NOCOUNT ON

	IF @FPARTY = '' SET @FPARTY = '0000000000'

	IF @TPARTY = '' SET @TPARTY = 'ZZZZZZZZZZ'

	DECLARE @@RECCOUNT INT

	SET @@RECCOUNT = 0

	SELECT @@RECCOUNT = COUNT(1) FROM SETTPAYOUT WHERE LEFT(REQUESTDT,11) = LEFT(GETDATE(),11) AND STATUS = 'P'

	IF @COMP_FOR = 'BRANCH'
	BEGIN
		IF @@RECCOUNT <> 0
		BEGIN
			SELECT ':: SORRY!! CANNOT PROCEED. THERE ARE SOME REQUESTS PENDING. PLEASE REJECT/APPROVE TO RECALCULATE ::'
			RETURN
		END
	END

	/*===========================================================================
	CREATE # TABLES FOR CANNED LEDGER BALANCES AND BILL AMOUNTS
	===========================================================================*/
	CREATE TABLE #LEDGERBALANCE
	(
		PARTYCODE VARCHAR(10),
		PARTYNAME VARCHAR(100),
		CHEQUENAME VARCHAR(100),
		BRANCHCODE VARCHAR(10),
		ACCAT VARCHAR(10),
		BTOBPAYMENT INT,
		TOTLEDBAL MONEY,
		NSELEDBAL MONEY,
		BSELEDBAL MONEY,
		FNOLEDBAL MONEY,
		SETT_NO VARCHAR(7),
		NORMALBILLAMT MONEY,
		T2TBILLAMT MONEY,
		BILLAMT MONEY,
		FUTUREPAYOUT MONEY,
		SHORTAGE MONEY,
		AMOUNTTOBEPAID MONEY,
		PAYMODE VARCHAR(1),
		BANKCODE VARCHAR(10),
		GENFLAG VARCHAR(1),
		CLIENTTYPE VARCHAR(3),
		FAMILYCODE VARCHAR(10),
		RUNDATE DATETIME
	)

	CREATE INDEX [PARTYCODE] ON [DBO].[#LEDGERBALANCE]([PARTYCODE]) ON [PRIMARY]

	CREATE TABLE [#LEDGERBILLTMP]
	(
		[CLTCODE] [VARCHAR] (10)  NULL,
		[BRANCHCODE] [VARCHAR] (10)  NULL,
		[LONGNAME] [VARCHAR] (100)  NULL,
		[ACCAT] [CHAR] (10)  NULL,
		[BTOBPAYMENT] [INT] NULL,
		[PAYMODE] [CHAR] (1)  NULL,
		[POBANKCODE] [VARCHAR] (10)  NULL,
		[SETT_NO] [VARCHAR] (7) NOT NULL,
		[NORMALBILLAMT] [MONEY],
		[T2TBILLAMT] [MONEY],
		[CLIENTTYPE] [VARCHAR] (3),
		[FAMILYCODE] [VARCHAR] (10),
		[RUNDATE] [DATETIME]
	) ON [PRIMARY]

	CREATE INDEX [CLTCODE] ON [DBO].[#LEDGERBILLTMP]([CLTCODE]) ON [PRIMARY]

	IF @COMP_FOR = 'BRANCH'
	BEGIN
		IF @GENFLAG = 'A' -- ADHOC PAYOUT
		BEGIN
			INSERT INTO
				#LEDGERBILLTMP
			SELECT
				A.CLTCODE,
				A.BRANCHCODE,
				LONGNAME,
				ACCAT,
				BTOBPAYMENT,
				PAYMODE,
				POBANKCODE,
				SETT_NO = '',
				NORMALBILLAMT = 0,
				T2TBILLAMT = 0,
				CLIENTTYPE = '',
				FAMILYCODE = '',
				RUNDATE = GETDATE()
			FROM
				ACMAST A WITH(NOLOCK)
			WHERE
				A.CLTCODE BETWEEN @FPARTY AND @TPARTY
				AND A.ACCAT IN (4, 104)
				AND A.POBANKCODE IN
				(
					SELECT
					CLTCODE
					FROM ACMAST
					WITH(NOLOCK)
					WHERE ACCAT = 2
				)
			GROUP BY
				A.CLTCODE,
				A.BRANCHCODE,
				LONGNAME,
				ACCAT,
				BTOBPAYMENT,
				PAYMODE,
				POBANKCODE

			UPDATE
				#LEDGERBILLTMP
			SET
				CLIENTTYPE = C1.CL_TYPE,
				FAMILYCODE = C1.FAMILY
			FROM
				MSAJAG..CLIENT1 C1,
				MSAJAG..CLIENT2 C2,
				MSAJAG..CLIENTTYPE C3
			WHERE
				CLTCODE = C2.PARTY_CODE
				AND C1.CL_CODE = C2.CL_CODE
				AND C1.CL_TYPE = C3.CL_TYPE

			DELETE
				#LEDGERBILLTMP
			FROM MSAJAG..CLIENT1 C1,
				MSAJAG..CLIENT2 C2
			WHERE
				CLTCODE = C2.PARTY_CODE
				AND C1.CL_CODE = C2.CL_CODE
				AND C1.CL_TYPE IN ('MTF', 'PMS', 'WEB')
		END
		ELSE
		IF @GENFLAG = 'S' --SETTLEMENT PAYOUT CAPITAL
		BEGIN
			IF @SEGMENT <> 'FUTURES'
			BEGIN
				DECLARE @@SQL VARCHAR(1000)

				SELECT @@SQL = ""

				SELECT PARTY_CODE, SELL_BUY, AMOUNT, SETT_NO, SETT_TYPE INTO #ACCBILL FROM MSAJAG..ACCBILL WHERE 1 = 2

				IF @EXCHANGE = 'NSX'
				BEGIN
					SELECT @@SQL = "INSERT INTO #ACCBILL SELECT PARTY_CODE, SELL_BUY, AMOUNT, SETT_NO, SETT_TYPE FROM MSAJAG..ACCBILL WHERE SETT_NO = '" + @STLNO + "' "
				END

				IF @EXCHANGE = 'BSE'
				BEGIN
					SELECT @@SQL = "INSERT INTO #ACCBILL SELECT PARTY_CODE, SELL_BUY, AMOUNT, SETT_NO, SETT_TYPE FROM BSEDB..ACCBILL WHERE SETT_NO = '" + @STLNO + "' "
				END

				EXEC (@@SQL)

				INSERT INTO
					#LEDGERBILLTMP
				SELECT
					A.CLTCODE,
					A.BRANCHCODE,
					LONGNAME,
					ACCAT,
					BTOBPAYMENT,
					PAYMODE,
					POBANKCODE,
					SETT_NO = @STLNO,
					NORMALBILLAMT = SUM(
									CASE
									WHEN SETT_TYPE = (CASE WHEN @EXCHANGE = 'NSX' THEN 'N' ELSE 'D' END)
									THEN (
										CASE
										WHEN SELL_BUY = '1'
										THEN -AMOUNT
										ELSE AMOUNT
										END
									)
									ELSE 0
									END
									),
					T2TBILLAMT = SUM(
									CASE
									WHEN SETT_TYPE = (CASE WHEN @EXCHANGE = 'NSX' THEN 'W' ELSE 'C' END)
									THEN (
										CASE
										WHEN SELL_BUY = '1'
										THEN -AMOUNT
										ELSE AMOUNT
										END
									)
									ELSE 0
									END
									),
					CLIENTTYPE = '',
					FAMILYCODE = '',
					RUNDATE = GETDATE()
				FROM
					#ACCBILL L WITH(NOLOCK),
					ACMAST A WITH(NOLOCK)
				WHERE
					L.PARTY_CODE = A.CLTCODE
					AND A.CLTCODE BETWEEN @FPARTY AND @TPARTY
					AND A.ACCAT IN (4, 104)
					AND L.SETT_NO = @STLNO
					AND A.POBANKCODE IN
					(
						SELECT
						CLTCODE
						FROM ACMAST
						WITH(NOLOCK)
						WHERE ACCAT = 2
					)
				GROUP BY
					A.CLTCODE,
					A.BRANCHCODE,
					LONGNAME,
					ACCAT,
					BTOBPAYMENT,
					PAYMODE,
					POBANKCODE
				HAVING
					SUM(
					CASE
					WHEN SELL_BUY = '1'
					THEN -AMOUNT
					ELSE AMOUNT
					END
					) > 0

				UPDATE
					#LEDGERBILLTMP
				SET
					CLIENTTYPE = C1.CL_TYPE,
					FAMILYCODE = C1.FAMILY
				FROM
					MSAJAG..CLIENT1 C1,
					MSAJAG..CLIENT2 C2,
					MSAJAG..CLIENTTYPE C3
				WHERE
					CLTCODE = C2.PARTY_CODE
					AND C1.CL_CODE = C2.CL_CODE
					AND C1.CL_TYPE = C3.CL_TYPE

				DELETE
					#LEDGERBILLTMP
				FROM MSAJAG..CLIENT1 C1,
					MSAJAG..CLIENT2 C2
				WHERE
					CLTCODE = C2.PARTY_CODE
					AND C1.CL_CODE = C2.CL_CODE
					AND C1.CL_TYPE IN ('MTF', 'PMS', 'WEB')
			END
		END
	END

	IF @COMP_FOR = 'HO'
	BEGIN
		INSERT INTO
			#LEDGERBILLTMP
		SELECT
			A.CLTCODE,
			A.BRANCHCODE,
			LONGNAME,
			ACCAT,
			BTOBPAYMENT,
			PAYMODE,
			POBANKCODE,
			SETT_NO = '',
			NORMALBILLAMT = 0,
			T2TBILLAMT = 0,
			CLIENTTYPE = '',
			FAMILYCODE = '',
			RUNDATE = GETDATE()
		FROM
			ACMAST A WITH(NOLOCK),
			SETTPAYOUT P WITH(NOLOCK)
		WHERE
			A.CLTCODE = P.CLTCODE
			AND A.ACCAT IN (4, 104)
			AND STATUS = 'P'
                        AND A.POBANKCODE IN
			(
				SELECT
				CLTCODE
				FROM ACMAST
				WITH(NOLOCK)
				WHERE ACCAT = 2
			)
            AND GENFLAG = @GENFLAG
		GROUP BY
			A.CLTCODE,
			A.BRANCHCODE,
			LONGNAME,
			ACCAT,
			BTOBPAYMENT,
			PAYMODE,
			POBANKCODE

		UPDATE
			#LEDGERBILLTMP
		SET
			CLIENTTYPE = C1.CL_TYPE,
			FAMILYCODE = C1.FAMILY
		FROM
			MSAJAG..CLIENT1 C1,
			MSAJAG..CLIENT2 C2,
			MSAJAG..CLIENTTYPE C3
		WHERE
			CLTCODE = C2.PARTY_CODE
			AND C1.CL_CODE = C2.CL_CODE
			AND C1.CL_TYPE = C3.CL_TYPE
	END

	/*============================================================================
	CALCULATE NSE CASH LEDGER BALANCES FOR CLIENTS WITH CREDIT IN THE SELECTED SETTLEMENT / LEDGER
	============================================================================*/
	INSERT INTO
		#LEDGERBALANCE
	SELECT
		L.CLTCODE,
		ACNAME = A.LONGNAME,
		CHEQUENAME = A.LONGNAME,
		A.BRANCHCODE,
		A.ACCAT,
		A.BTOBPAYMENT,
		TOTLEDBAL = 0,
		NSELEDBAL = SUM(
					CASE
					WHEN EDT BETWEEN @SDATE + ' 00:00:00' AND @EFFDATE + ' 23:59:00'
					THEN (
						CASE
						WHEN DRCR = 'D'
						THEN -VAMT
						ELSE VAMT
						END
					)
					ELSE 0
					END
					) + SUM(
							CASE
							WHEN EDT > @EFFDATE + ' 23:59:00'
							THEN (
								CASE
								WHEN DRCR = 'D'
								THEN -VAMT
								ELSE 0
								END
						)
					ELSE 0
					END
					),
		BSELEDBAL = 0,
		FNOLEDBAL = 0,
		A.SETT_NO,
		A.NORMALBILLAMT,
		A.T2TBILLAMT,
		BILLAMT = A.NORMALBILLAMT + A.T2TBILLAMT,
		FUTUREPAYOUT = 0 ,
		SHORTAGE = 0,
		AMOUNTTOBEPAID = 0,
		A.PAYMODE,
		A.POBANKCODE,
		GENFLAG = @GENFLAG,
		A.CLIENTTYPE,
		A.FAMILYCODE,
		RUNDATE
	FROM
		#LEDGERBILLTMP A WITH(NOLOCK),
		ACCOUNT..LEDGER L WITH(NOLOCK)
	WHERE
		L.CLTCODE = A.CLTCODE
		AND L.VDT BETWEEN @SDATE + ' 00:00:00' AND @EFFDATE + ' 23:59:00'
	GROUP BY
		A.BRANCHCODE,
		L.CLTCODE,
		A.LONGNAME,
		A.ACCAT,
		A.BTOBPAYMENT,
		A.PAYMODE,
		A.POBANKCODE,
		A.SETT_NO,
		A.NORMALBILLAMT,
		A.T2TBILLAMT,
		A.CLIENTTYPE,
		A.FAMILYCODE,
		RUNDATE

	UNION ALL

	SELECT
		L.CLTCODE,
		ACNAME = A.LONGNAME,
		CHEQUENAME = A.LONGNAME,
		A.BRANCHCODE,
		A.ACCAT,
		A.BTOBPAYMENT ,
		TOTLEDBAL = 0,
		NSELEDBAL = SUM(
					CASE
					WHEN DRCR = 'D'
					THEN VAMT
					ELSE -VAMT
					END
					),
		BSELEDBAL = 0,
		FNOLEDBAL = 0,
		A.SETT_NO,
		A.NORMALBILLAMT,
		A.T2TBILLAMT,
		BILLAMT = A.NORMALBILLAMT + A.T2TBILLAMT,
		FUTUREPAYOUT = 0 ,
		SHORTAGE = 0,
		AMOUNTTOBEPAID = 0,
		A.PAYMODE,
		A.POBANKCODE,
		GENFLAG = @GENFLAG,
		A.CLIENTTYPE,
		A.FAMILYCODE,
		RUNDATE
	FROM
		#LEDGERBILLTMP A WITH(NOLOCK),
		ACCOUNT..LEDGER L WITH(NOLOCK)
	WHERE
		L.CLTCODE = A.CLTCODE
		AND L.EDT >= @EFFDATE + ' 23:59:00'
		AND L.VDT < @SDATE
	GROUP BY
		A.BRANCHCODE,
		L.CLTCODE,
		A.LONGNAME,
		A.ACCAT,
		A.BTOBPAYMENT,
		A.PAYMODE,
		A.POBANKCODE,
		A.SETT_NO,
		A.NORMALBILLAMT,
		A.T2TBILLAMT,
		A.CLIENTTYPE,
		A.FAMILYCODE,
		RUNDATE

	/*============================================================================
	CALCULATE BSE CASH LEDGER BALANCES FOR CLIENTS WITH CREDIT IN THE SELECTED SETTLEMENT / LEDGER
	============================================================================*/
	INSERT INTO
		#LEDGERBALANCE
	SELECT
		L.CLTCODE,
		ACNAME = A.LONGNAME,
		CHEQUENAME = A.LONGNAME,
		A.BRANCHCODE,
		A.ACCAT,
		A.BTOBPAYMENT ,
		TOTLEDBAL = 0,
		NSELEDBAL = 0,
		BSELEDBAL = SUM(
