		A.SETT_NO,
		A.NORMALBILLAMT,
		A.T2TBILLAMT,
		A.CLIENTTYPE,
		A.FAMILYCODE,
		RUNDATE

	UNION ALL

	SELECT
		L.CLTCODE,
		ACNAME = A.LONGNAME,
		CHEQUENAME = A.LONGNAME,
		A.BRANCHCODE,
		A.ACCAT,
		A.BTOBPAYMENT ,
		TOTLEDBAL = 0,
		NSELEDBAL = 0,
		BSELEDBAL = SUM(
					CASE
					WHEN DRCR = 'D'
					THEN VAMT
					ELSE -VAMT
					END
					),
		FNOLEDBAL = 0,
		A.SETT_NO,
		A.NORMALBILLAMT,
		A.T2TBILLAMT,
		BILLAMT = A.NORMALBILLAMT + A.T2TBILLAMT,
		FUTUREPAYOUT = 0 ,
		SHORTAGE = 0,
		AMOUNTTOBEPAID = 0,
		A.PAYMODE,
		A.POBANKCODE,
		GENFLAG = @GENFLAG,
		A.CLIENTTYPE,
		A.FAMILYCODE,
		RUNDATE
	FROM
		#LEDGERBILLTMP A WITH(NOLOCK),
		ACCOUNTBSE..LEDGER L WITH(NOLOCK)
	WHERE
		L.CLTCODE = A.CLTCODE
		AND L.EDT >= @EFFDATE + ' 23:59:00'
		AND L.VDT < @SDATE
	GROUP BY
		A.BRANCHCODE,
		L.CLTCODE,
		A.LONGNAME,
		A.ACCAT,
		A.BTOBPAYMENT,
		A.PAYMODE,
		A.POBANKCODE,
		A.SETT_NO,
		A.NORMALBILLAMT,
		A.T2TBILLAMT,
		A.CLIENTTYPE,
		A.FAMILYCODE,
		RUNDATE

