create PROC [dbo].[V2_OFFLINE_JVDRCR_UPLOAD]
	@FNAME VARCHAR(100),
	@UNAME VARCHAR(25),
	@VTYP SMALLINT,
	@BOOKTYPE VARCHAR(2),
	@EXCHANGE VARCHAR(3),
    @SEGMENT  VARCHAR(10),
	@STATUSID VARCHAR(15),
	@STATUSNAME VARCHAR(15)
 

