-- --------------------------------------------------
-- PROCEDURE dbo.V2_OFFLINE_RECPAYUPLOAD
-- --------------------------------------------------
            
CREATE PROC [dbo].[V2_OFFLINE_RECPAYUPLOAD](                                                                
                                                                
           @UNAME     VARCHAR(25),                                                                
           @USERCAT   INT,                                                                
           @EXCHANGE  VARCHAR(3),                                                                
           @SEGMENT   VARCHAR(10),                                                                
           @IP_VDATE varchar(11),                                              
           @RECOFLAG  CHAR(1) = 'N')                                                                
                                                                
AS                                                                
                                        
SET XACT_ABORT ON                                                                
/*                                                              
V2_OFFLINE_RECPAYUPLOAD 'OFFLINE',1,'NSE','CAPITAL','N'                                                             
*/                                                              
                                                              
                                                              
  SET NOCOUNT ON                                                                
                                                                  
  /*---------VALIDATION FOR SINGLE VOUCHER TYPE BASED ON DRCR --------*/                                                                
  DECLARE  @@STD_DATE        VARCHAR(11),                                                                
           @@LST_DATE        VARCHAR(11),                                                                
           @@BRANCHFLAG       AS TINYINT,                                                                
           @@VNOFLAG          AS TINYINT,                                                                
           @@VNOMETHOD       INT,                                                                
           @@ACNAME          CHAR(100),                                                                
           @@BOOKTYPE        CHAR(2),                                                                
           @@MICRNO          VARCHAR(10),                                                                
           @@DRCR            VARCHAR(1),                                                                
           @@VTYP            SMALLINT,                                                                
           @@BANK_CODE       VARCHAR(100),                                                                
           @@BACKDAYS        INT,                                                                
           @@BACKDATE        VARCHAR(11),                                                                
           @@BRNCOUNT        TINYINT,                                                                
           @@MonthlyVdt      VARCHAR(11),                                                                
           @@SERIAL_NO       INT,                                                                
           @@COSTCODECOUNT   TINYINT,                                                                
           @@COSTCODEUPDATES CURSOR,                                                                
           @@NEWVNO           AS VARCHAR(12),                                                                
           @@MAXCOUNT         AS INT,                                                                
           @@VDT              AS VARCHAR(11),                                                                
           @@BANKBRANCH       AS VARCHAR(10),                                                                
           @@BANKCOST         AS NUMERIC,                         
           @@LNOCUR           AS CURSOR,                                         
           @@VNOCUR           AS CURSOR,                                                     
           @@LNOVNO           AS VARCHAR(12),                                                                
           @@MAXVNO           AS INT,                                                             
           @@L2CUR            AS CURSOR,                                                        
           @@L2VNO            AS VARCHAR(12),                                                              
           @@ERROR_COUNT      AS BIGINT,                                                
           @@FILEEXISTS       AS INT,                                           
           @@SQL              AS VARCHAR(2000),                                             
           @ERRORCODE         AS INT,                                                                
     @MESSAGE           AS VARCHAR(200) ,                                                        
   @@RECPAYTBLCOUNT BIGINT                                                    
                                                                                     
                                            
 SET @@VTYP = 2                                                              
                                                              
BEGIN DISTRIBUTED TRANSACTION                                                                
                                                                
  CREATE TABLE [#RECPAY_TABLE] (                                                             
    SNO    INT   IDENTITY ( 1,1 ),                                                                
    EDATE        VARCHAR(20),                                                                
    VOUCHER_DATE VARCHAR(20),                                                                
    CLIENT_CODE  VARCHAR(50),                                                                
    AMOUNT       MONEY,                                                                
    DRCR         VARCHAR(1),                                               
    NARRATION    VARCHAR(234),                                                                
    BANK_CODE    VARCHAR(10),                                                                
    BANK_NAME    VARCHAR(100),                                                                
    REF_NO       VARCHAR(15),                                                                
    BRANCHCODE   VARCHAR(20),                                                                
    BRANCH_NAME  VARCHAR(50),                                                                
    CHQ_MODE     VARCHAR(1),                                                                
    CHQ_DATE     VARCHAR(20),                                                           
    CHQ_NAME     VARCHAR(100),                                                                
    CL_MODE      VARCHAR(1),                                                                
    FLD_AUTO     BIGINT,                                                         
    ENTEREDBY    VARCHAR(25),                                                                
    VDT          DATETIME   NULL,                                                                
    FYSTART      DATETIME   NULL,                                                          
    FYEND        DATETIME   NULL,                                                                
    UPDFLAG      VARCHAR(1)   NOT NULL   DEFAULT ('N'),                                                                
    EDT          DATETIME   NULL,                                                                
    DDDT         DATETIME   NULL,                                                                
    VNO          VARCHAR(12)   NULL,                                                                
   VTYP         SMALLINT   NULL,                                                                
    ACNAME       VARCHAR(100)   NULL,                                           
    COSTCODE     SMALLINT   NULL,                                                                
    BANKCOST     SMALLINT   NULL,                                                              
    LNO          SMALLINT   NOT NULL DEFAULT (0),                                                                
    BANKNAME     VARCHAR(100)   NULL,                                                                
    MICRNO       INT   NULL,                                                                
    BOOKTYPE     VARCHAR(2)   NULL,                                                                
    ACC_NO       VARCHAR(20))                                                                
  ON [PRIMARY]                                   
                                                          
  /* POPULATE APPROVED RECORDS FROM OFFLINE ENTRIES TABLE TO TEMP TABLE FOR THE EXCHANGE-SEGMENT */                                                  
  INSERT INTO #RECPAY_TABLE                                                                
             (EDATE,                 
              VOUCHER_DATE,                                       
              CLIENT_CODE,                                                                
              AMOUNT,                                   
              DRCR,                                                                
              NARRATION,                                                                
              BANK_CODE,                                                              
              BANK_NAME,                                                                
              REF_NO,                                                                
              BRANCHCODE,                                                 
              BRANCH_NAME,                                                                
              CHQ_MODE,                                                                
              CHQ_DATE,                                 
              CHQ_NAME,                                                                
              CL_MODE,                                                                
              FLD_AUTO,                                                                
              ENTEREDBY,                                                                
