           ENTEREDBY                 
--PRINT 'NIKHIL'              
                                
                                                                  
/*======INSERTING ALL LEDGER RECORDS AT ONE TIME======*/                                                                
  INSERT INTO LEDGER                                           
             (VTYP,                                                                
              VNO,                                                                
              EDT,                                               
              LNO,                                                                
              ACNAME,                                                                
              DRCR,                                                                
              VAMT,                                                                
              VDT,                                                                
              VNO1,                                                                
              REFNO,                                                               
              BALAMT,                                                              
          NODAYS,                                                                
              CDT,                                                                
              CLTCODE,                                                                
              BOOKTYPE,                       
              ENTEREDBY,                                                                
              PDT,                                                                
              CHECKEDBY,                     
              ACTNODAYS,                  
 NARRATION)                                                                
