           VAMT,                                                                
           VDT,                                                                
           VNO1,                                                                
           REFNO,                                                                
           BALAMT,                          
           NODAYS,                                                                
           CDT,                                                                
           CLTCODE,                                               
           BOOKTYPE,                            
