    EXEC ACCOUNT_AB.DBO.V2_OFFLINE_UPLOAD_PUTLOG  @EXCHANGE, @SEGMENT, 2, @ERRORCODE, @MESSAGE, @UNAME                                                            
    RETURN                                                       
    END                                                                                                          
  DROP TABLE #LEDGER3                                                                
                                                                  
  IF @@BRANCHFLAG = 1                                                                
    BEGIN                                                                
      SET @@L2CUR = CURSOR FOR SELECT   DISTINCT VNO                                                                
              FROM     #RECPAY_TABLE                                                                
         ORDER BY VNO                                                                
                                                 
      OPEN @@L2CUR                                                                
                                                                      
      FETCH NEXT FROM @@L2CUR                                                                
      INTO @@L2VNO                   
                                                                    
      WHILE @@FETCH_STATUS = 0                                                    BEGIN                                                                
          DELETE FROM TEMPLEDGER2                                                                
          WHERE       SESSIONID = '9999999999'                                                                
                                                                          
/*======CLIENT SIDE RECORD======*/                                                                
          INSERT INTO TEMPLEDGER2                                                                
          SELECT 'BRANCH',                                                                
                 C.COSTNAME,                                             
                 AMOUNT,                                                                
                 VTYP,                              
                 VNO,                                             
                 LNO,                                                                
                 DRCR,                                                                
                 '0',                             
    BOOKTYPE,                                                                
                 '9999999999',                                                                
                 CLIENT_CODE,                                                                
'A',                                                                
                 '0'                                                                
          FROM   #RECPAY_TABLE RP,                                                                
                 COSTMAST C WITH(NOLOCK)                                                                 
          WHERE  RP.COSTCODE = C.COSTCODE                                                                
                 AND VNO = @@L2VNO                                                                
                                                       
/*======BANK SIDE RECORD======*/                                                                
          INSERT INTO TEMPLEDGER2                                                                
          SELECT   'BRANCH',                                                                
                   C.COSTNAME,                                                                
                   SUM(AMOUNT),                                                                
                   VTYP,                                                                
           VNO,                                                            
                   LNO = 1,                                                                
                   DRCR = (CASE                                                                 
                             WHEN DRCR = 'D' THEN 'C'                                                                
                       ELSE 'D'                                                                
                           END),                                                                
                   '0',                                                                
                   BOOKTYPE,                                    
                   '9999999999',                                                                
                   BANK_CODE,                                                                
                   'A',                                                                
                   '0'                                                                
