 WHERE M.MCLTCODE=L.CLTCODE  
 AND M.VTYP='18' AND M.VNO LIKE @@VNO + '%'  
 GROUP BY MCLTCODE,CLTCODE  
 )M  
 GROUP BY CLTCODE    
 HAVING SUM(AMOUNT) <> 0

GO

